type LoadingText = {
    text: string
}

export default function Loading({text} : LoadingText){
    const bgColor = {
        background:"linear-gradient(0deg, #4a4eff, #aeb2ff)"
    }

    return (
      <div className="loading">
        <div className="w-[100px] h-[100px] rounded-full mb-10 flex items-center justify-center animate-spin"
            style={bgColor}
        >
          <div className="rounded-full bg-white w-[90px] h-[90px]" />
        </div>
        <h2>{text}</h2>
      </div>
    )
  }
