import { ChangeEvent, useState } from "react";
import { useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck,faXmark,faChevronLeft,faGear,faLanguage,faSun,faMoon,faExclamationCircle } from "@fortawesome/free-solid-svg-icons";
import { Button } from "../components/button";
import InvisibleOverlay from "../components/drawers/invisible-overlay";

interface SearchSec {
  isOpen: boolean
  isAnimated: boolean
  result: string
}

interface PageSec {
  current: number
  title: string
  isChanging: boolean
}

interface Timer {
  isSwitched: boolean
  isSixty: boolean
  defaultTimer: number
}

export default function MainPage(){

  const { lang } = useParams()

  const [pageSec, setPageSec] = useState<PageSec>({
    current: 0,
    title: "Let's Guess",
    isChanging: false
  })

  const [searchSec, setSearchSec] = useState<SearchSec>({
    isOpen: false,
    isAnimated: false,
    result: "",
  })

  const [timer, setTimer] = useState<Timer>({
    isSwitched: false,
    isSixty: true,
    defaultTimer: 60
  })

  const toggleTimer = (e: ChangeEvent<HTMLInputElement>) => {
    localStorage.setItem("timer-switch", timer.isSwitched.toString());
    localStorage.setItem("timer-sixty", timer.isSixty.toString());
    setTimer(prevTimer => ({ ...prevTimer, isSwitched: e.target.checked }));

    if (!timer.isSwitched){
      setTimer(prevTimer => ({ ...prevTimer, isSwitched: true, isSixty: true }))
      localStorage.setItem("timer", String(120));
      localStorage.setItem("timer-continue", String(120));
    } else {
      setTimer(prevTimer => ({ ...prevTimer, isSwitched: false, isSixty: false }))
      localStorage.setItem("timer", String(60));
      localStorage.setItem("timer-continue", String(60));
    }
  }

  const clickSearch = {
    open: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' });
      setTimeout(() => {
        setSearchSec({ isOpen: true, isAnimated: false, result: '' })
      }, 300)
    },
    close: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' })
      setTimeout(() => {
        setSearchSec({ isOpen: false, isAnimated: false, result: '' })
      }, 300)
    }
  }

  return (
    <div className="App">
      {pageSec.isChanging && <InvisibleOverlay />}

      <header>
        <h1 id="heading" className={`${searchSec.isOpen ? 'active' : ''}`}>{pageSec.title}</h1>
      </header>
      <main>
        <div id="how-to-play">
          <h2>How to play:</h2>
          <ol>
            <li>The hint giver can only see the answer on the screen, and try to hint the answer so that the players can guess what the answer is.</li>
            <li>The hint giver can hide the answer by clicking 'Hide answer' button at the top right of the screen.</li>
            <li>If the players guess correctly, press <FontAwesomeIcon icon={faCheck}/> button to go to the next word.</li>
            <li>If the players guess incorrectly or don't know the answer, press <FontAwesomeIcon icon={faXmark}/> button to skip the word.</li>
            <li>You have 60 or 120 seconds, depending on your setting, to hint the answers.</li>
          </ol>
        </div>
      </main>
      <footer className={`app-foot ${pageSec.isChanging ? 'active' : pageSec.current > 0 ? 'hide-footer' : 'inactive'}${pageSec.isChanging ? ' start' : ''}`}>
        <h2>Are you ready?</h2>
        <Button onClick={() => {}}>Play now</Button>
      </footer>
    </div>
  )
}
