import { useState } from "react"
import { CountType } from "../types/main-page"

const usePoint = () => {
  const pointStart = localStorage.getItem("point")
  const [point, setPoint] = useState<CountType>(pointStart !== null ? parseInt(pointStart) : null)

  const countPoint = () => {
    localStorage.setItem("point", String(point && point + 1))
  }

  return { point, setPoint, countPoint }
}

export default usePoint
