import { IconProp } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { ReactNode } from "react";

interface ButtonProps {
  className?: string
  children: ReactNode
  size?: string
  onClick?: () => void
  onMouseEnter?: () => void
  onMouseLeave?: () => void
}

interface ButtonWithIconProps {
  animation?: string
  className?: string
  children: ReactNode
  icon: IconProp
  text: string
}

enum ButtonSize {
  Small = "small",
  Medium = "medium",
  Icon = "icon"
}

export function Button({ className, children, size, onClick, onMouseEnter } : ButtonProps){
  return (
    <button 
      className={`${size === ButtonSize.Small ? 'small-btn' : size === ButtonSize.Medium ? 'medium-btn' : size === ButtonSize.Icon ? 'icon-btn' : 'btn'} ${className || ''}`}
      onClick={onClick}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
    >
      {children}
    </button>
  )
}

export function ButtonWithIcon({ animation, className, children, icon, text } : ButtonWithIconProps){
  return (
    <button className={`btn btn-with-icon ${className}`}>
      <FontAwesomeIcon icon={icon} className="icon-prefix-btn"/>
      {text}
    </button>
  )
}
