import { useState } from "react";
import { useParams } from "react-router-dom";

interface SearchSec {
  isOpen: boolean
  isAnimated: boolean
  result: string
}

export default function MainPage(){
  const [title, setTitle] = useState("Let's Guess")
  const [currentSec, setCurrentSec] = useState(0)

  const [searchSec, setSearchSec] = useState<SearchSec>({
    isOpen: false,
    isAnimated: false,
    result: "",
  })

  const { lang } = useParams()

  const clickSearch = {
    open: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' });
      setTimeout(() => {
        setSearchSec({ isOpen: true, isAnimated: false, result: '' })
      }, 300)
    },
    close: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' })
      setTimeout(() => {
        setSearchSec({ isOpen: false, isAnimated: false, result: '' })
      }, 300)
    }
  }

  return (
    <div>

    </div>
  )
}
