import { ChangeEvent, useState } from "react";
import { useParams } from "react-router-dom";

interface SearchSec {
  isOpen: boolean
  isAnimated: boolean
  result: string
}

interface PageSec {
  current: number
  title: string
  isChanging: boolean
}

interface Timer {
  isSwitched: boolean
  isSixty: boolean
  defaultTimer: number
}

export default function MainPage(){
  const [pageSec, setPageSec] = useState<PageSec>({
    current: 0,
    title: "Let's Guess",
    isChanging: false
  })

  const [searchSec, setSearchSec] = useState<SearchSec>({
    isOpen: false,
    isAnimated: false,
    result: "",
  })

  const [timer, setTimer] = useState<Timer>({
    isSwitched: false,
    isSixty: true,
    defaultTimer: 60
  })

  const { lang } = useParams()

  const toggleTimer = (e: ChangeEvent<HTMLInputElement>) => {
    localStorage.setItem("timer-switch", timer.isSwitched.toString());
    localStorage.setItem("timer-sixty", timer.isSixty.toString());
    setTimer(prevTimer => ({ ...prevTimer, isSwitched: e.target.checked }));
    if (!timer.isSwitched){
      setTimerSixty(true);
      setTimerSwitch(true);
      localStorage.setItem("timer", 120);
      localStorage.setItem("timer-continue", 120);
    } else {
      setTimerSixty(false);
      setTimerSwitch(false);
      localStorage.setItem("timer", 60);
      localStorage.setItem("timer-continue", 60);
    }
  }

  const clickSearch = {
    open: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' });
      setTimeout(() => {
        setSearchSec({ isOpen: true, isAnimated: false, result: '' })
      }, 300)
    },
    close: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' })
      setTimeout(() => {
        setSearchSec({ isOpen: false, isAnimated: false, result: '' })
      }, 300)
    }
  }

  return (
    <div>

    </div>
  )
}
