import { ChangeEvent, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { englishText, thaiText } from "../data/languages";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck,faXmark,faChevronLeft,faGear,faLanguage,faSun,faMoon,faExclamationCircle } from "@fortawesome/free-solid-svg-icons";
import { Button } from "../components/button";
import InvisibleOverlay from "../components/drawers/invisible-overlay";
import { startStorages } from "../data/initial-storages";
import { CardCateWithTitle } from "../components/card";
import { cateLists, cateTitles } from "../data/categories-en";
import { cateLists as cateListsThai, cateTitles as cateTitlesThai } from "../data/categories-th";

interface SearchSec {
  isOpen: boolean
  isAnimated: boolean
  result: string
}

interface PageSec {
  current: number
  isChanging: boolean
  isLoaded: boolean
}

interface Timer {
  isSwitched: boolean
  isSixty: boolean
  defaultTimer: number
}

enum LanguagesEnum {
  Thai = "th",
  Chinese = "zh",
  German = "de"
}

enum BackgroundChange {
  GreenToPink = "green_to_pink 900ms forwards",
  GreenToPinkDark = "green_to_pink_dark 900ms forwards",
  PinkToGreen = "pink_to_green 900ms forwards",
  PinkToGreenDark = "pink_to_green_dark 900ms forwards"
}

export default function MainPage(){

  const { lang } = useParams()
  const [mainLang, setMainLang] = useState(lang == LanguagesEnum.Thai ? {
    language: thaiText,
    categories: cateTitlesThai,
    cateList: cateListsThai
  } : {
    language: englishText,
    categories: cateTitles,
    cateList: cateLists
  })

  const [pageSec, setPageSec] = useState<PageSec>({
    current: 0,
    isChanging: false,
    isLoaded: true
  })

  const [searchSec, setSearchSec] = useState<SearchSec>({
    isOpen: false,
    isAnimated: false,
    result: "",
  })

  const [timer, setTimer] = useState<Timer>({
    isSwitched: false,
    isSixty: true,
    defaultTimer: 60
  })

  const toggleTimer = (e: ChangeEvent<HTMLInputElement>) => {
    localStorage.setItem("timer-switch", timer.isSwitched.toString());
    localStorage.setItem("timer-sixty", timer.isSixty.toString());
    setTimer(prevTimer => ({ ...prevTimer, isSwitched: e.target.checked }));

    if (!timer.isSwitched){
      setTimer(prevTimer => ({ ...prevTimer, isSwitched: true, isSixty: true }))
      localStorage.setItem("timer", String(120));
      localStorage.setItem("timer-continue", String(120));
    } else {
      setTimer(prevTimer => ({ ...prevTimer, isSwitched: false, isSixty: false }))
      localStorage.setItem("timer", String(60));
      localStorage.setItem("timer-continue", String(60));
    }
  }

  const changePage = (page: number, animation: string) => {
    setPageSec(prev => ({...prev, isChanging: true}))
    document.body.style.animation = animation
    setTimeout(() => {
      setPageSec(prev => ({...prev, current: page, isChanging: false}))
    }, 900)
  }

  const clickSearch = {
    open: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' });
      setTimeout(() => {
        setSearchSec({ isOpen: true, isAnimated: false, result: '' })
      }, 300)
    },
    close: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' })
      setTimeout(() => {
        setSearchSec({ isOpen: false, isAnimated: false, result: '' })
      }, 300)
    }
  }

  useEffect(() => {
    localStorage.length == 0 && startStorages()

    setTimeout(() => {
      setPageSec(prevSec => ({ ...prevSec, isLoaded: false }))
    }, 900)
  }, [pageSec])

  return (
    <div className="App">
      {pageSec.isChanging && <InvisibleOverlay />}

      <header className={`app-head ${pageSec.isChanging ? 'active' : 'inactive'}${pageSec.isLoaded ? ' start' : ''}`}>
        <h1 id="heading" className={`${searchSec.isOpen ? 'active' : ''}`}>
          {pageSec.current === 1 ? mainLang.language.select_category : mainLang.language.title}
        </h1>
      </header>

      <main className={`app-main ${pageSec.isChanging ? 'active' : 'inactive'}${pageSec.isLoaded ? ' start' : ''}`}>
        {pageSec.current === 1 ? (
          <section className={`select-list ${searchSec.isOpen ? 'switching' : ''}`}>

            {mainLang.categories.map((cate: any) => (
              <CardCateWithTitle key={cate.key || cate} title={cate.title || cate} data={mainLang.cateList[cate.dataKey] || mainLang.cateList[cate.toLowerCase()]} onSelect={() => {}}/>
            ))}

          </section>
        ) : (
          <div id="how-to-play">
            <h2>{mainLang.language.how_to_play_title}</h2>

            {mainLang.language.how_to_play_desc}

          </div>
        )}
      </main>

      <footer className={`app-foot ${pageSec.isChanging ? 'active' : pageSec.current > 0 ? 'hide-footer' : 'inactive'}${pageSec.isLoaded ? ' start' : ''}`}>
        <h2>{mainLang.language.ready}</h2>
        <Button onClick={() => changePage(1, BackgroundChange.GreenToPink)}>{mainLang.language.play_now}</Button>
      </footer>
    </div>
  )
}
