import { ChangeEvent, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { englishText, thaiText } from "../languages/languages";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck,faXmark,faChevronLeft,faGear,faLanguage,faSun,faMoon,faExclamationCircle } from "@fortawesome/free-solid-svg-icons";
import { Button } from "../components/button";
import InvisibleOverlay from "../components/drawers/invisible-overlay";
import { startStorages } from "../data/initial-storages";

interface SearchSec {
  isOpen: boolean
  isAnimated: boolean
  result: string
}

interface PageSec {
  current: number
  isChanging: boolean
  isLoaded: boolean
}

interface Timer {
  isSwitched: boolean
  isSixty: boolean
  defaultTimer: number
}

export default function MainPage(){

  const { lang } = useParams()
  const [mainLang, setMainLang] = useState(lang === "th" ? thaiText : englishText)

  const [pageSec, setPageSec] = useState<PageSec>({
    current: 0,
    isChanging: false,
    isLoaded: true
  })

  const [searchSec, setSearchSec] = useState<SearchSec>({
    isOpen: false,
    isAnimated: false,
    result: "",
  })

  const [timer, setTimer] = useState<Timer>({
    isSwitched: false,
    isSixty: true,
    defaultTimer: 60
  })

  const toggleTimer = (e: ChangeEvent<HTMLInputElement>) => {
    localStorage.setItem("timer-switch", timer.isSwitched.toString());
    localStorage.setItem("timer-sixty", timer.isSixty.toString());
    setTimer(prevTimer => ({ ...prevTimer, isSwitched: e.target.checked }));

    if (!timer.isSwitched){
      setTimer(prevTimer => ({ ...prevTimer, isSwitched: true, isSixty: true }))
      localStorage.setItem("timer", String(120));
      localStorage.setItem("timer-continue", String(120));
    } else {
      setTimer(prevTimer => ({ ...prevTimer, isSwitched: false, isSixty: false }))
      localStorage.setItem("timer", String(60));
      localStorage.setItem("timer-continue", String(60));
    }
  }

  const clickSearch = {
    open: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' });
      setTimeout(() => {
        setSearchSec({ isOpen: true, isAnimated: false, result: '' })
      }, 300)
    },
    close: () => {
      setSearchSec({ isOpen: true, isAnimated: true, result: '' })
      setTimeout(() => {
        setSearchSec({ isOpen: false, isAnimated: false, result: '' })
      }, 300)
    }
  }

  useEffect(() => {
    localStorage.length === 0 && startStorages()

    setTimeout(() => {
      setPageSec(prevSec => ({ ...prevSec, isLoaded: false }))
    }, 900)
  }, [pageSec])

  return (
    <div className="App">
      {pageSec.isChanging && <InvisibleOverlay />}

      <header className={`app-head ${pageSec.isChanging ? 'active' : 'inactive'}${pageSec.isLoaded ? ' start' : ''}`}>
        <h1 id="heading" className={`${searchSec.isOpen ? 'active' : ''}`}>{mainLang.title}</h1>
      </header>

      <main className={`app-main ${pageSec.isChanging ? 'active' : 'inactive'}${pageSec.isLoaded ? ' start' : ''}`}>
        <div id="how-to-play">
          <h2>{mainLang.how_to_play.title}</h2>
          {mainLang.how_to_play.desc}
        </div>
      </main>

      <footer className={`app-foot ${pageSec.isChanging ? 'active' : pageSec.current > 0 ? 'hide-footer' : 'inactive'}${pageSec.isLoaded ? ' start' : ''}`}>
        <h2>{mainLang.ready}</h2>
        <Button onClick={() => {}}>{mainLang.play_now}</Button>
      </footer>
    </div>
  )
}
