import { useState } from "react";
import { ChangeEvent } from "react";

const useTimer = () => {

  const checkIfTimer = localStorage.getItem("timer")
  const [timerSwitch, setTimerSwitch] = useState(localStorage.getItem("timer") == "120" ? true : false)
  const [timerStart, setTimerStart] = useState<number | null>(null)

  if (checkIfTimer !== null && timerStart !== null){
    setTimerStart(parseInt(checkIfTimer))
  }

  const [isTimeUp, setIsTimeUp] = useState(false)

  const toggleTimer = (e: ChangeEvent<HTMLInputElement>) => {
    setTimerSwitch(e.target.checked);

    if (!timerSwitch){
      localStorage.setItem("timer", String(120));
      localStorage.setItem("timer-continue", String(120));
    } else {
      localStorage.setItem("timer", String(60));
      localStorage.setItem("timer-continue", String(60));
    }
  }

  const countTimer = () => {
    const timer = setInterval(() => {
      if (timerStart !== null){
        if (timerStart > 0){
          setTimerStart(timerStart - 1)
          localStorage.setItem("timer-continue", String(timerStart - 1))
        }

        if (timerStart == 0){
          setIsTimeUp(true)
          setTimeout(() => {
            setIsTimeUp(false)
          }, 2000)

          clearInterval(timer)
        }
      }
    }, 1000)

    return () => {
      clearInterval(timer)
    }
  }

  return { timerSwitch, toggleTimer, countTimer, isTimeUp, timerStart }
}

export default useTimer
